import React from 'react'
import {Typography, Grid, Box, Paper} from '@material-ui/core';
import { ResponsiveLine } from '@nivo/line'

function sumByDate(list) {
  const map = new Map();
  list.forEach((item) => {
    const key = item.date;
    const collection = map.get(key);
    map.set(key, item.amount + (
      collection
      ? collection
      : 0));
  });
  return map;
}

Date.prototype.addDays = function(days) {
    var date = new Date(this.valueOf());
    date.setDate(date.getDate() + days);
    return date;
}

function addMissingDays(data){
  const parsedDates = data.map(d => new Date(d));
  let firstDay =  parsedDates[0];
  const nDays = Math.round((parsedDates[-1] - firstDay)/(24*60*60*1000));

  const newData = [data[0]];

  for (i =0; i<nDays; i++) {
    firstDay = firstDay + 24*60*60*1000;
    const ind = parsedDates.indexOf(firstDay.toISOString().slice(0,10));
    if (ind === -1) {
      newData.push([firstDay, 0]);
    } else {
      newData.push(data[ind]);
    }
  }
  return newData;
}

// make sure parent container have a defined height when using
// responsive component, otherwise height will be 0 and
// no chart will be rendered.
// website examples showcase many properties,
// you'll often use just a few of them.
const MyResponsiveBar = ({ chartData /* see data tab */ }) => (
  <Box height="300px" width="100%">
    <ResponsiveLine
      data={[
          {
              id: 'fake corp. A',
              data: addMissingDays(chartData)
          },
      ]}
      enableArea={true}
      pointSize={5}
      enableCrosshair={false}
      margin={{ bottom: 100 }}
      padding="10px"
      enableGridX={false}
      enableGridY={false}
      xScale={{
          type: 'time',
          format: '%Y-%m-%d',
          precision: 'day',
      }}
      xFormat="time:%Y-%m-%d"
      axisBottom={{
          format: '%b %d',
          tickValues: 'every 2 days',
          tickRotation: 60,
      }}
      pointBorderWidth={1}
      useMesh={true}
      enableSlices={false}
    />
  </Box>
)

export default class DailyView extends React.PureComponent {
  constructor(props) {
    super(props);

    this.state = {
      data: Array.from(sumByDate(props.transactions)).map(day => {return ({x: day[0], y: day[1]})}).reverse()
    };
  }

  render() {
    const {data: chartData} = this.state;

    if (!chartData) {
      return null;
    }

    console.log(chartData);

    return (<MyResponsiveBar chartData={chartData} />);
  }
}
