import React from 'react';
import ReactDOM from 'react-dom';
import {Container} from '@material-ui/core';
import TitleBar from './components/TitleBar'
import Summary from './components/Summary'

import './App.css';

function App() {
  const getPlaidAccounts = () => {
    const storedAccounts = window.localStorage.getItem("plaid_account");
    if (storedAccounts) {return storedAccounts.json()}
    else {return false}
  }

  const [accounts, setAccounts] = React.useState(getPlaidAccounts);

  return (
    <Container maxWidth="sm">
        <TitleBar addedAccountChecker={()=> setAccounts(getPlaidAccounts())}/>
        <Summary account={accounts} />
    </Container>
  );
}

export default App;
