import React from 'react'
import {Typography, Grid} from '@material-ui/core';
import {makeStyles} from '@material-ui/core/styles';
import {Alert, AlertTitle} from '@material-ui/lab';
import Bank from './Bank';

const useStyles = makeStyles((theme) => ({
	bank_grid: {
		marginTop: "30px",
		marginBottom: "30px"
	},
}))

function Summary({accounts}) {
	const classes = useStyles();

  if (accounts) {
    return (
			<Grid container justify="center" spacing={2} className={classes.bank_grid}>
          {accounts.map((account) => (
            <Grid key={account.name} item>
              <Bank {...account} />
            </Grid>
          ))}
        </Grid>
		);
  } else {
    return (<Alert severity="info">
      <AlertTitle>You currently have no accounts.</AlertTitle>
      Press the 'add' button to connect your first current account.
    </Alert>)
  }
}

export default Summary;
