import React from 'react'
import {Typography, Grid, Paper} from '@material-ui/core';
import {Chart, BarSeries, Title, ArgumentAxis, ValueAxis} from '@devexpress/dx-react-chart-material-ui';
import {Animation} from '@devexpress/dx-react-chart';

function sumByDate(list) {
  const map = new Map();
  list.forEach((item) => {
    const key = item.date;
    const collection = map.get(key);
    map.set(key, item.amount + (
      collection
      ? collection
      : 0));
  });
  return map;
}

export default class DailyView extends React.PureComponent {
  constructor(props) {
    super(props);

    this.state = {
      data: Array.from(sumByDate(props.transactions))
    };
  }

  render() {
    const {data: chartData} = this.state.map(day => {return {date: day[0], spend: day[1]}});

    if (!chartData) {
      return null;
    }

    return (<Paper>
      <Chart data={chartData}>
        <ArgumentAxis/>
        <ValueAxis max={7}/>

        <BarSeries valueField="spent" argumentField="date"/>
        <Title text="World population"/>
        <Animation/>
      </Chart>
    </Paper>);
  }
}
