import React from 'react'
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableFooter,
  TablePagination,
  TableRow,
  TableHead,
  IconButton,
  Paper
} from '@material-ui/core';
import {makeStyles, useTheme} from '@material-ui/core/styles';
import {KeyboardArrowLeft, KeyboardArrowRight} from '@material-ui/icons';
import FirstPageIcon from '@material-ui/icons/FirstPage';
import LastPageIcon from '@material-ui/icons/LastPage';

import PropTypes from 'prop-types';

const useStyles = makeStyles({
  table: {
    minWidth: 650,
  },
});

function TransactionsList({transactions}) {
  const classes = useStyles();

  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(5);

  const emptyRows = rowsPerPage - Math.min(rowsPerPage, transactions.length - page * rowsPerPage);

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  return (<TableContainer component={Paper}>
    <Table className={classes.table} aria-label="custom pagination table" size="small">
      <TableHead>
          <TableRow>
            <TableCell>Dessert (100g serving)</TableCell>
            <TableCell align="right">Calories</TableCell>
            <TableCell align="right">Fat&nbsp;(g)</TableCell>
          </TableRow>
        </TableHead>
      <TableBody>
        {
          (
            rowsPerPage > 0
            ? transactions.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
            : transactions).map((row) => (<TableRow key={row.name}>
              <TableCell component="th" scope="row">
                {row.name}
              </TableCell>
              <TableCell align="right">{row.calories}</TableCell>
              <TableCell align="right">{row.fat}</TableCell>
            </TableRow>))
        }

        {
          emptyRows > 0 && (<TableRow style={{
              height: 53 * emptyRows
            }}>
            <TableCell colSpan={6}/>
          </TableRow>)
        }
      </TableBody>
      <TableFooter>
        <TableRow>
          <TablePagination rowsPerPageOptions={[
              5,
              10,
              25, {
                label: 'All',
                value: -1
              }
            ]} colSpan={3} count={transactions.length} rowsPerPage={rowsPerPage} page={page} onChangePage={handleChangePage} onChangeRowsPerPage={handleChangeRowsPerPage}/>
        </TableRow>
      </TableFooter>
    </Table>
  </TableContainer>);

}

export default TransactionsList;
