import React from 'react'
import {Typography, Grid, Paper} from '@material-ui/core';
import {
  Chart,
  BarSeries,
  Title,
  ArgumentAxis,
  ValueAxis,
} from '@devexpress/dx-react-chart-material-ui';
import { Animation } from '@devexpress/dx-react-chart';

function sumByDate(list) {
    const map = new Map();
    list.forEach((item) => {
         const key = item.date;
         const collection = map.get(key);
         map.set(key, item.amount + (collection ? collection : 0));
    });
    return map;
}

function DailyView ({ transactions }) {
  if (!transactions) {
    const { data: chartData } = transactions;
    return (
      <Paper>
        <Chart
          data={chartData}
        >
          <ArgumentAxis />
          <ValueAxis max={7} />

          <BarSeries
            valueField="population"
            argumentField="year"
          />
          <Title text="World population" />
          <Animation />
        </Chart>
      </Paper>
    );
  }

}
export default DailyView;
