import React from 'react'
import {Typography, Grid, Box, Paper} from '@material-ui/core';
import { ResponsiveLine } from '@nivo/line'

function sumByDate(list) {
  const map = new Map();
  list.forEach((item) => {
    const key = item.date;
    const collection = map.get(key);
    map.set(key, item.amount + (
      collection
      ? collection
      : 0));
  });
  return map;
}



// make sure parent container have a defined height when using
// responsive component, otherwise height will be 0 and
// no chart will be rendered.
// website examples showcase many properties,
// you'll often use just a few of them.
const MyResponsiveBar = ({ chartData /* see data tab */ }) => (
  <Box height="300px" width="100%">
    <ResponsiveLine
      data={[
          {
              id: 'fake corp. A',
              data: chartData
          },
      ]}
      margin={{ top: 50, right: 60, bottom: 100, left: 60 }}
      padding={0.3}
      xScale={{
          type: 'time',
          format: '%Y-%m-%d',
          precision: 'day',
      }}
      xFormat="time:%Y-%m-%d"
      axisBottom={{
          format: '%b %d',
          tickValues: 'every 2 days',
          tickRotation: 60,
      }}
      enablePointLabel={true}
      pointSize={16}
      pointBorderWidth={1}
      pointBorderColor={{
          from: 'color',
          modifiers: [['darker', 0.3]],
      }}
      useMesh={true}
      enableSlices={false}
    />
  </Box>
)

export default class DailyView extends React.PureComponent {
  constructor(props) {
    super(props);

    this.state = {
      data: Array.from(sumByDate(props.transactions)).map(day => {return ({x: day[0], y: day[1]})}).reverse()
    };
  }

  render() {
    const {data: chartData} = this.state;

    if (!chartData) {
      return null;
    }

    console.log(chartData);

    return (<MyResponsiveBar chartData={chartData} />);
  }
}
