import React from 'react';
import ReactDOM from 'react-dom';
import {Container} from '@material-ui/core';
import TitleBar from './components/TitleBar'
import Summary from './components/Summary'

import './App.css';

function App() {
  const getPlaidAccount = () => {window.localStorage.getItem("plaid_account")}
  const [account, setAccount] = React.useState(getPlaidAccount);

  return (
    <Container maxWidth="sm">
        <TitleBar addedAccountChecker={()=> setAccount(getPlaidAccount())}/>
        <Summary account={account} />
    </Container>
  );
}

export default App;
