import React from 'react'
import {Typography, Grid, Box, Paper} from '@material-ui/core';
import { ResponsiveLine } from '@nivo/line'

function sumByDate(list) {
  const map = new Map();
  list.forEach((item) => {
    const key = item.date;
    const collection = map.get(key);
    map.set(key, item.amount + (
      collection
      ? collection
      : 0));
  });
  return map;
}



// make sure parent container have a defined height when using
// responsive component, otherwise height will be 0 and
// no chart will be rendered.
// website examples showcase many properties,
// you'll often use just a few of them.
const MyResponsiveBar = ({ data /* see data tab */ }) => (
  <Box height="300px" width="100%">
    <ResponsiveLine
        data={data}
        keys={[ 'spend' ]}
        indexBy="date"
        margin={{ top: 50, right: 60, bottom: 100, left: 60 }}
        padding={0.3}
        colors={{ scheme: 'nivo' }}
        defs={[
            {
                id: 'dots',
                type: 'patternDots',
                background: 'inherit',
                color: '#38bcb2',
                size: 4,
                padding: 1,
                stagger: true
            },
            {
                id: 'lines',
                type: 'patternLines',
                background: 'inherit',
                color: '#eed312',
                rotation: -45,
                lineWidth: 6,
                spacing: 10
            }
        ]}
        borderColor={{ from: 'color', modifiers: [ [ 'darker', 1.6 ] ] }}
        axisTop={null}
        axisRight={null}
        xScale={{ "type": "time", "format": "native", "precision": "day" }}
        yScale={{ type: 'linear', min: 'auto', max: 'auto', stacked: true, reverse: false }}
        axisBottom={{
            tickSize: 2,
            tickPadding: 5,
            tickRotation: 60,
            legend: 'date',
            legendPosition: 'middle',
            legendOffset: 80,
        }}
        axisLeft={{
            tickSize: 5,
            tickPadding: 5,
            tickRotation: 0,
            legend: 'Daily Spend',
            legendPosition: 'middle',
            legendOffset: -50
        }}
        labelSkipWidth={12}
        labelSkipHeight={12}
        labelTextColor={{ from: 'color', modifiers: [ [ 'darker', 1.6 ] ] }}
        animate={true}
        motionStiffness={90}
        motionDamping={15}
    />
  </Box>
)

export default class DailyView extends React.PureComponent {
  constructor(props) {
    super(props);

    this.state = {
      data: Array.from(sumByDate(props.transactions)).map(day => {return ({x: new Date(day[0]), y: day[1]})})
    };
  }

  render() {
    const {data: chartData} = this.state;

    if (!chartData) {
      return null;
    }

    console.log(chartData);

    return (<MyResponsiveBar data={chartData} />);
  }
}
